DROP TABLE IF EXISTS `pauses`;
CREATE TABLE `pauses` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `meeting` bigint(20) unsigned NOT NULL,
  `start` datetime NOT NULL,
  `end` datetime DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `meeting` (`meeting`),
  CONSTRAINT `pauses_ibfk_2` FOREIGN KEY (`meeting`) REFERENCES `meetings` (`id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=utf16 COLLATE=utf16_german2_ci;

